import {
  ChangeDetectionStrategy,
  Component,
  input,
  output,
} from '@angular/core';

import { Card } from './model';

@Component({
  selector: 'ngs-card',
  standalone: true,
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    '[style.backgroundColor]': 'card().color',
  },
  styles: `
    :host {
      width: 100%;
      aspect-ratio: 2/3;
      display: block;
      position: relative;
      border-radius: 1rem;
      max-width: 10vw;
      min-width: 50px;

      // background-color: grey;
    }

    .delete-btn {
      --icon: url(data:image/svg+xml;base64,);
      position: absolute;
      bottom: -0.75rem;
      right: -0.75rem;
      width: 3rem;
      height: 3rem;
      padding: 0.5rem;
      border: 4px solid;
      border-radius: 100%;
      background: aliceblue var(--icon) no-repeat 50% 50% / 70%;
      color: white;
      cursor: pointer;

      &:hover {
        background-color: orangered;
      }
    }

    .sr-only {
      border: 0;
      clip: rect(1px, 1px, 1px, 1px);
      clip-path: inset(50%);
      height: 1px;
      margin: -1px;
      overflow: hidden;
      padding: 0;
      position: absolute;
      width: 1px;
      white-space: nowrap;
    }
  `,
  template: `
    <button class="delete-btn" type="button" (click)="deleteCard.emit(card())">
      <span class="sr-only">Delete</span>
    </button>
  `,
})
export class CardComponent {
  card = input.required<Card>();

  deleteCard = output<Card>();
}
